import React from "react";
import api from "../api";
import PostCard from "./postCard";
import { useEffect } from "react";
import { useState } from "react";
import Posts from "./posts";

const PostsPage = () => {
  const [users, setUsers] = useState();
  const [posts, setPosts] = useState();
  useEffect(() => {
    api.data.fetchAllUsers().then((data) => setUsers(data));
  }, [users]);

  useEffect(() => {
    api.posts.fetchAllPosts().then((data) => setUsers(data));
  }, [posts]);

  return users && posts ? (
    <>
      <h1 className="m-4 p-2">Posts</h1>
      {posts.map((post) => {
        return (
          <div key={post.userId} className="card m-4 p-2">
            <h3>{user.name + " " + user.surname}</h3>
            {user.posts.map((post) => {
              return <PostCard key={post.id} post={post} userId={user.id} />;
            })}
          </div>
        );
      })}
    </>
  ) : (
    "Loading"
  );
};

export default PostsPage;
