import React, { useEffect, useContext } from "react";
import TextField from "../fields/textField";
import api from "../../api/index";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../../App";
import axios from "axios";

const LoginForm = () => {
  //   const isLogged = useContext(UserContext);

  const [isLogged, setIsLogged] = useState();
  const [success, setSuccess] = useState(false);
  const [users, setUsers] = useState();
  const [data, setData] = useState({
    login: "",
    password: "",
  });

  const navigate = useNavigate();

  useEffect(() => {
    let isMounted = true;
    api.data.getIsLogged().then((data) => setIsLogged(data));
    return () => {
      isMounted = false;
    };
  }, []);

  const handleLogin = (id) => {
    api.data.setIsLogged(id).then((data) => console.log(data));
  };
  console.log(isLogged);

  useEffect(() => {
    let isMounted = true;
    api.data.fetchAllUsers().then((data) => setUsers(data));
    return () => {
      isMounted = false;
    };
  }, []);

  const handleChange = (target) => {
    setData((prevState) => ({
      ...prevState,
      [target.name]: target.value,
    }));
    console.log(data);
  };

  const handleSubmit = async (e) => {
    setSuccess(true);
    e.preventDefault();
    console.log(users);
    console.log(data);

    axios
      .post(
        "http://127.0.0.1:8000/api/v1/token/",
        {
          username: "user110",
          password: "DKfdcji46",
        },
        { withCredentials: true }
      )
      .then(
        (response) =>
          (axios.defaults.headers.common[
            "Authorization"
          ] = `Bearer ${response.access}`)
      );

    const registeredUser = users.find((user) => {
      return user.login === data.login && user.password === data.password;
    });

    // if (registeredUser) {
    navigate(`/profile/${registeredUser.id}`, { replace: true });
    handleLogin(registeredUser.id);
    //   document.location.reload();
    // } else {
    //   console.log("user not found");
    // }
  };

  return (
    !isLogged && (
      <div className="m-4 p-4">
        <section className="shadow m-4 p-4">
          <form className="g-3" onSubmit={handleSubmit}>
            <h1>Login Form</h1>
            <TextField
              type="text"
              label="Логин"
              name="login"
              onChange={handleChange}
              value={data.login}
            />

            <TextField
              type="password"
              label="Пароль"
              name="password"
              onChange={handleChange}
              value={data.password}
            />

            <div className="col-12">
              <button
                // onClick={handleClick}
                // type="submit"
                className="btn m-2 btn-sm btn-outline-dark"
              >
                Sign in
              </button>
            </div>
          </form>
        </section>
      </div>
    )
  );
};

export default LoginForm;
