import axios from "axios";

const inMemoryJWTService = () => {
  let inMemoryJWT = null;
  let refreshTimeoutId = null;

  const refreshToken = () => {
    const timeoutTrigger = 5 * 60 * 1000 - 10000;

    refreshTimeoutId = setInterval(() => {
      axios
        .post("http://127.0.0.1:8000/api/v1/refresh/", refreshToken)
        .then((response) => setToken(response.data.access));
    }, timeoutTrigger);
  };

  const abortRefeshToken = () => {
    if (refreshTimeoutId) clearInterval(refreshTimeoutId);
  };

  const getToken = () => inMemoryJWT;

  const getRefreshToken = () => refreshToken;

  const setRefreshToken = (token) => {
    const refreshToken = token;
  };

  const setToken = (token) => {
    inMemoryJWT = token;
    refreshToken();
  };

  const deleteToken = () => {
    inMemoryJWT = null;
    abortRefeshToken();
  };

  return getToken, setToken, deleteToken, setRefreshToken;
};

export default inMemoryJWTService();
