import axios from "axios";

export const refreshToken = () => {
  let refreshTimeoutId = null;

  refreshTimeoutId = setInterval(() => {
    let refresh = localStorage.getItem("refresh");
    console.log(refresh);
    axios
      .post("http://127.0.0.1:8000/api/v1/refresh/", JSON.stringify(refresh))
      .then((response) => {
        console.log(response.data);
        localStorage.removeItem("access");
        localStorage.setItem("access", response.data);
      })
      .catch((err) => console.log(err));
  }, 5000);

  const abortRefeshToken = () => {
    if (refreshTimeoutId) clearInterval(refreshTimeoutId);
  };
};

