import React, { useEffect, useState, useContext } from "react";
import api from "../api/fake.api/users.api";
import { useNavigate } from "react-router-dom";
import CommentForm from "./commentForm";
import { v4 as uuidv4 } from "uuid";
import axios from "axios";
import CustomScroll from "react-custom-scroll";

const PostCard = ({ postId }) => {
  const [isLogged, setIsLogged] = useState();
  const navigate = useNavigate();
  const [post, setPost] = useState();
  const [categories, setCategories] = useState();
  const [comments, setComments] = useState();
  const [newComments, setNewComments] = useState();
  const [users, setUsers] = useState();
  const [newPost, setNewPost] = useState({});
  const [newCategories, setNewCategories] = useState([]);
  const [newUser, setNewUser] = useState();

  useEffect(() => {
    let isMounted = true;
    api.getPostById(postId).then((data) => {
      setPost(data);
    });

    // try {
    //   axios
    //     .get(`http://127.0.0.1:8000/api/v1/Posts/1/`)
    //     .then((data) => setNewPost(data.data));
    // } catch (error) {
    //   console.log(error);
    // }
    // console.log(post);
    return () => {
      isMounted = false;
    };
  }, []);

  useEffect(() => {
    let isMounted = true;
    api.getIsLogged().then((data) => setIsLogged(data));
    return () => {
      isMounted = false;
    };
  }, []);

  useEffect(() => {
    let isMounted = true;
    api.fetchAllCategories().then((data) => {
      setCategories(data);
    });

    //   try {
    //     axios
    //       .get("http://127.0.0.1:8000/api/v1/categories/")
    //       .then((data) => setNewCategories(data.data));
    //   } catch (error) {
    //     console.log(error);
    //   }

    return () => {
      isMounted = false;
    };
  }, []);

  useEffect(() => {
    let isMounted = true;
    api.fetchAllUsers().then((data) => {
      setUsers(data);
    });

    // try {
    //   axios
    //     .get("http://127.0.0.1:8000/api/v1/User/")
    //     .then((data) => setNewUser(data.data));
    // } catch (error) {
    //   console.log(error);
    // }
    return () => {
      isMounted = false;
    };
  }, []);

  useEffect(() => {
    let isMounted = true;
    api.getCommentsByPostId(postId).then((data) => {
      setComments(data);
    });
    // axios
    //   .get(`http://127.0.0.1:8000/api/v1/Posts/${"1"}/comments/`)
    //   .then((data) => console.log(data.data))
    //   .catch((err) => console.log(err));
    // console.log(comments);
    return () => {
      isMounted = false;
    };
  }, []);

  const handleEdit = () => {
    navigate(`/home/${postId}/edit`);
  };

  //get category by post id
  const getCategoryBadge = (item) => {
    //     try {
    //       axios
    //         .get(`http://127.0.0.1:8000/api/v1/Posts/1/category/`)
    //         .then((data) => console.log(data.data));
    //     } catch (error) {
    //       console.log(error);
    //     }

    // "badge bg-" + item.color
    // "#d49b5e"
    // #555a24
    // #606131
    return (
      <span
        className={"badge"}
        style={{ backgroundColor: "#606131" }}
        key={item.id}
      >
        {item.name}
      </span>
    );
  };

  //   const setUserName = (userId) => {
  //     api.getUserById(userId).then((data) => {
  //       setUser(data);
  //     });
  //     return user;
  //   };

  //комметарий + login юзера
  const getCommentLoginByPostId = (postId) => {
    // try {
    //   axios
    //     .get(`http://127.0.0.1:8000/api/v1/Posts/2/comments/`)
    //     .then((data) => console.log(data.data));
    // } catch (error) {
    //   console.log(error);
    // }
  };

  const getComments = (comment) => {
    const user = users.find((user) => user.id === comment.userId);
    return (
      <div key={comment.commentId}>
        <p style={{ fontStyle: "italic" }}>
          {user.name + " " + user.surname}: {comment.commentText}
        </p>
        {isLogged && comment.userId === isLogged && (
          <button
            type="button"
            onClick={() => handleCommentDelete(comment.commentId)}
            className="btn btn-danger m-2"
          >
            Удалить
          </button>
        )}
      </div>
    );
  };

  const handlePostDelete = (id) => {
    // try {
    //   axios
    //     .delete(`http://127.0.0.1:8000/api/v1/Posts/${1}`)
    //     .then((response) => console.log(response.data));
    // } catch (error) {
    //   console.log(error);
    // }

    api.deletePost(id);
    document.location.reload();
  };

  const handleCommentDelete = (id) => {
    //???
    api.deleteComment(id);
    document.location.reload();
  };

  //   const renderTextClass = () => {
  // 	if
  //   }

  //   const handleTextBtnClick = () => {

  //   }

  //   const divideText = (text) => {
  //     const visiblePart = text.substring((0, 10));
  //     const invisiblePart = text.substring(10, text.length());
  //     if (visiblePart === text) {
  //       return <p className="card-text">{post.text}</p>;
  //     } else {
  //       return (
  //         <>
  //           <p>{visiblePart}</p>
  //           <span className="more-text">...</span>
  //           <p className="more-text">{invisiblePart}</p>
  //           <button onClick={handleTextBtnClick} className="more-btn">Читать далее</button>
  //         </>
  //       );
  //     }
  //   };

  return post && categories && comments ? (
    <div className="p-4">
      <div className="">
        {categories.map((category) => {
          if (category.id === post.categoryId) {
            return getCategoryBadge(category);
          }
        })}
      </div>
      <div className="">
        <h2 className="card-title">{post.title}</h2>
        <p className="card-text">{post.text}</p>

        {isLogged && post.userId === isLogged && (
          <div className="group-button">
            <button onClick={handleEdit} className="btn btn-success m-2">
              Редактировать пост
            </button>
            <button
              type="button"
              onClick={() => handlePostDelete(post.id)}
              className="btn btn-danger m-2"
            >
              Удалить
            </button>
          </div>
        )}
        {comments && (
          <div className="card-footer">
            <p>Комментарии</p> {comments.map((comment) => getComments(comment))}
          </div>
        )}
      </div>
    </div>
  ) : (
    ""
  );
};

export default PostCard;
