import React from "react";
import TextField from "./textField";
import { useState } from "react";
import TextAreaField from "./textAreaField";
import { useEffect } from "react";
import api from "../api";

const AddPost = ({ userId, postId }) => {
  const [post, setPost] = useState({ id: postId, title: "", text: "" });
  console.log(post);
  const [user, setUser] = useState();

  useEffect(() => {
    api.data.getById(userId).then((data) => {
      setUser(data);
    });
  }, []);

  console.log();

  const handleChange = (target) => {
    setPost((prevState) => ({
      ...prevState,
      [target.name]: target.value,
    }));
  };

  const handleClick = () => {
    api.data.update(userId, post).then((data) => console.log(data));
  };

  return user ? (
    <>
      <h1>add post</h1>
      <div className="m-4 p-4 shadow">
        <TextField
          type="text"
          label="Название поста"
          name="title"
          value={post.title}
          onChange={handleChange}
        />
        <TextAreaField
          label="Содержание поста"
          name="text"
          value={post.text}
          onChange={handleChange}
        />
        <button
          onClick={handleClick}
          type="submit"
          className="btn btn-primary mt-2 mb-2"
          //   disabled={!isValid}
        >
          Опубликовать
        </button>
      </div>
    </>
  ) : (
    "loading"
  );
};

export default AddPost;
