import React from "react";
import api from "../api/fake.api/users.api";
import { useHistory } from "react-router-dom/cjs/react-router-dom";

const PostCard = ({ post, userId }) => {
  const handleClick = () => {
    api.deletePost(userId, post.id);
  };
  const history = useHistory();

  const handlePostPage = () => {
    history.push(`/posts/${post.id}`);
  };

  const handleEdit = () => {
    history.push(`/home/${userId}/edit/${post.id}`);
  };

  return (
    <div className="card mb-2">
      <div className="card-header">header</div>
      <div className="card-body">
        <h5 className="card-title">{post.title}</h5>
        <p className="card-text">{post.text}</p>
        <button onClick={handlePostPage} className="btn btn-primary m-2">
          Перейти к посту
        </button>

        {/* <button onClick={handleEdit} className="btn btn-success m-2">
          Редактировать пост
        </button> */}

        <button
          type="button"
          onClick={handleClick}
          className="btn btn-danger m-2"
        >
          Удалить
        </button>
      </div>
    </div>
  );
};

export default PostCard;
