import React, { useContext } from "react";
import api from "../api";
import PostCard from "./postCard";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";
import CommentForm from "./commentForm";
import backgroundImage from "../images/background.av1.avif";

const PostsPage = () => {
  const [isLogged, setIsLogged] = useState();
  const [users, setUsers] = useState([]);
  const [userPosts, setUserPosts] = useState([]);

  useEffect(() => {
    let isMounted = true;

    if (isMounted) {
      try {
        axios
          .get("http://127.0.0.1:8000/api/v1/Posts/")
          .then((data) => setPosts(data.data));

        console.log(posts);
      } catch (error) {
        console.log(error);
      }
    }

    // api.data.fetchAllPosts().then((data) => {
    //   if (isMounted) setPosts(data);
    // });
    return () => {
      isMounted = false;
    };
  }, []);

  useEffect(() => {
    let isMounted = true;
    // api.data.fetchAllUsers().then((data) => {
    //   if (isMounted) setUsers(data);
    // });

    try {
      axios
        .get("http://127.0.0.1:8000/api/v1/User/")
        .then((data) => setUsers(data.data));
    } catch (error) {
      console.log(error);
    }

    return () => {
      console.log(users);
      isMounted = false;
    };
  }, []);

  useEffect(() => {
    let isMounted = true;
    api.data.getIsLogged().then((data) => setIsLogged(data));
    return () => {
      isMounted = false;
    };
  }, []);

  const getComments = (comment) => {
    const user = users.find((user) => user.id === comment.userId);
    return (
      <div key={comment.commentId} className="m-2">
        <p>{user.login}</p>s<p>{comment.commentText}</p>
      </div>
    );
  };

  //   const getPosts = () => {
  //     users.map(async (user) => {
  //       let data = await axios.get(
  //         `http://127.0.0.1:8000/api/v1/User/${user.id}/post/`
  //       );
  //       setUserPosts(data.data || null);
  //       console.log(userPosts);
  //       return userPosts && <div>{userPosts.title}</div>;
  //     });
  //   };

  return users && posts ? (
    <div className="m-4 p-4">
      <div className="shadow m-4 p-4">
        <div
          className="main-page"
          style={{
            backgroundImage: `url(${backgroundImage}`,
            backgroundRepeat: "no-repeat",
            backgroundSize: "contain",
            height: 500,
            width: 700,
            margin: "auto",
          }}
        >
          <div className="title justify-content-center">
            <h1 className="pt-4">Beyond Ruins</h1>
          </div>
        </div>
        <div>
          <h1 className="text-center">Posts</h1>

          {users.map((user) => {
            return (
              posts.find((post) => post.userId === user.id) &&
              user.id !== isLogged && (
                <div key={user.id} className="m-4 p-2 card">
                  <span className="post-author">
                    <i className="bi bi-suitcase-lg">{" " + user.username}</i>
                  </span>
                  {posts.map((post) => {
                    if (post.userId === user.id) {
                      return (
                        post && (
                          <div key={post.id}>
                            <PostCard postId={post.id} />
                            {isLogged && (
                              <CommentForm postId={post.id} userId={user.id} />
                            )}
                          </div>
                        )
                      );
                    }
                  })}
                </div>
              )
            );
          })}
        </div>
      </div>
    </div>
  ) : (
    "Loading"
  );
};

export default PostsPage;
