import axios from "axios";

export const setAuthToken = () => {
  let token = localStorage.getItem("access");
  if (token) {
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
  } else delete axios.defaults.headers.common["Authorization"];
};
