import React, { useEffect, useState, useContext } from "react";
import api from "../api/fake.api/users.api";
import { useNavigate } from "react-router-dom";
import CommentForm from "./commentForm";
import axios from "axios";

const PostCard = ({ postId }) => {
  const [isLogged, setIsLogged] = useState(
    localStorage.getItem("refresh") ? true : false
  );
  const navigate = useNavigate();
  const [post, setPost] = useState();
  const [category, setCategory] = useState();
  const [comments, setComments] = useState([]);
  const [users, setUsers] = useState();

  useEffect(() => {
    let isMounted = true;
    axios
      .get(`http://127.0.0.1:8000/api/v1/Posts/${postId}/`)
      .then((data) => setPost(data.data))
      .catch((err) => console.log(err));
    return () => {
      console.log(post);
      isMounted = false;
    };
  }, []);

  useEffect(() => {
    let isMounted = true;
    axios
      .get(`http://127.0.0.1:8000/api/v1/Posts/${postId}/category/`)
      .then((data) => setCategory(data.data))
      .catch((err) => console.log(err));
    return () => {
      isMounted = false;
    };
  }, []);

  useEffect(() => {
    let isMounted = true;
    api.getCommentsByPostId(postId).then((data) => {
      setComments(data);
    });
    axios
      .get(`http://127.0.0.1:8000/api/v1/Posts/${postId}/comments/`)
      .then((data) => setComments(data.data))
      .catch((err) => console.log(err));
    console.log(comments);
    return () => {
      isMounted = false;
    };
  }, []);

  const handleEdit = () => {
    navigate(`/home/${postId}/edit`);
  };

  const getCategoryBadge = (item) => {
    return (
      <span
        className={"badge"}
        style={{ backgroundColor: "#555a24" }}
        key={post.id}
      >
        {item.name}
      </span>
    );
  };

  //комметарий + login юзера
  const getComments = () => {
    try {
      axios
        .get(`http://127.0.0.1:8000/api/v1/Posts/${postId}/comments/`)
        .then((data) => setComments(data.data || null));
    } catch (error) {
      console.log(error);
    }
    console.log(comments);

    // const user = users.find((user) => user.id === comment.userId);
    // return (
    //   <div key={comment.commentId}>
    //     <p style={{ fontStyle: "italic" }}>
    //       {user.username}: {comment.commentText}
    //     </p>
    //     {isLogged && comment.userId === isLogged && (
    //       <button
    //         type="button"
    //         onClick={() => handleCommentDelete(comment.commentId)}
    //         className="btn m-2 btn-sm btn-outline-danger"
    //       >
    //         Удалить
    //       </button>
    //     )}
    //   </div>
    // );
  };

  const handlePostDelete = (id) => {
    // try {
    //   axios
    //     .delete(`http://127.0.0.1:8000/api/v1/Posts/${1}`)
    //     .then((response) => console.log(response.data));
    // } catch (error) {
    //   console.log(error);
    // }

    api.deletePost(id);
    document.location.reload();
  };

  const handleCommentDelete = (id) => {
    //???
    api.deleteComment(id);
    document.location.reload();
  };

  return post && category && comments ? (
    <div className="p-4">
      <div>{getCategoryBadge(category)}</div>
      <div>
        <h2 className="card-title">{post.title}</h2>
        <p className="card-text">{post.text}</p>

        {isLogged && (
          <div className="group-button">
            <div className="d-grid d-md-flex justify-content-md-end">
              <button
                onClick={handleEdit}
                className="btn btn-outline-dark m-2 btn-sm"
              >
                Редактировать пост
              </button>
              <button
                type="button"
                onClick={() => handlePostDelete(post.id)}
                className="btn m-2 btn-sm btn-outline-danger"
              >
                Удалить
              </button>
            </div>
          </div>
        )}
        {comments && (
          <div className="card-footer">
            <p>Комментарии</p>{" "}
            {Object.values(comments).map((comment) => getComments(comment))}
          </div>
        )}
      </div>
    </div>
  ) : (
    ""
  );
};

export default PostCard;
