import React, { useEffect, useContext, useRef } from "react";
import TextField from "../fields/textField";
import api from "../../api/index";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import AuthContext from "../../context/AuthProvider";
import axios from "../../api-2/axios";

const LOGIN_URL = "/auth";

const LoginForm = () => {
  const { setAuth } = useContext(AuthContext);
  const userRef = useRef();
  const errRef = useRef();

  const [success, setSuccess] = useState(false);
  const [errMsg, setErrMsg] = useState("");
  const [users, setUsers] = useState();
  const [data, setData] = useState({
    email: "",
    password: "",
  });

  const navigate = useNavigate();

  useEffect(() => {
    userRef.current.focus();
  }, []);

  useEffect(() => {
    setErrMsg("");
  }, [data]);

  useEffect(() => {
    api.data.fetchAllUsers().then((data) => setUsers(data));
  }, []);

  const handleChange = (target) => {
    setData((prevState) => ({
      ...prevState,
      [target.name]: target.value,
    }));
    console.log(data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(users);
    console.log(data);
    setSuccess(true);

    try {
      const response = await axios.post(LOGIN_URL, JSON.stringify(data), {
        headers: { "Content-Type": "application/json" },
        withCredentials: true,
      });
      console.log(JSON.stringify(response?.data));
      console.log(JSON.stringify(response));
      const accessToken = response?.data?.accessToken;
      setAuth(...data, accessToken);
      setData({});
      setSuccess(true);
    } catch (error) {
      if (!error.response) {
        setErrMsg("no server response");
      } else if (error.response?.status === 400) {
        setErrMsg("missing email / password");
      } else if (error.response?.status === 401) {
        setErrMsg("unauthorized");
      } else {
        setErrMsg("login failed");
      }
      errRef.current.focus();
    }

    const registeredUser = users.find((user) => {
      return user.email === data.email && user.password === data.password;
    });
    if (registeredUser) {
      navigate.replace(`/home/${registeredUser.id}`);
    } else {
      console.log("user not found");
    }
  };

  return (
    <div className="shadow m-4 p-4">
      <p
        ref={errRef}
        className={errMsg ? "errmsg" : "offscreen"}
        aria-live="assertive"
      >
        {errMsg}
      </p>
      <form className="g-3" onSubmit={handleSubmit}>
        <h1>Login Form</h1>
        <TextField
          type="text"
          label="Email"
          name="email"
          onChange={handleChange}
          value={data.email}
        />

        <TextField
          type="password"
          label="Пароль"
          name="password"
          onChange={handleChange}
          value={data.password}
        />

        <div className="col-12">
          <button
            // onClick={handleClick}
            // type="submit"
            className="btn btn-primary mt-2 mb-2"
          >
            Sign in
          </button>
          <p>
            Need an aacount?{" "}
            <span className="line">
              {" "}
              <a href="#">Sign up</a>
            </span>
          </p>
        </div>
      </form>
    </div>
  );
};

export default LoginForm;
