import React, { useEffect, useState } from "react";
import api from "../api/fake.api/users.api";
import { useHistory } from "react-router-dom/cjs/react-router-dom";

const PostCard = ({ postId }) => {
  //   console.log(postId);
  const history = useHistory();
  const [post, setPost] = useState();
  const [categories, setCategories] = useState();

  useEffect(() => {
    let isMounted = true;
    api.getPostById(postId).then((data) => {
      setPost(data);
    });
    return () => {
      isMounted = false;
    };
  }, []);

  useEffect(() => {
    let isMounted = true;
    api.fetchAllCategories().then((data) => {
      setCategories(data);
    });
    return () => {
      isMounted = false;
    };
  });

  console.log(categories);

  const handleEdit = () => {
    history.push(`/home/${postId}/edit`);
  };

  const setCategoryClassName = (item) => {
    return "badge m-1 bg-" + item.color;
  };

  const handleClick = () => {
    api.deletePost(postId);
  };

  return post && categories ? (
    <div className="card mb-2">
      <div className="card-header">header</div>
      <div className="card-body">
        <h5 className="card-title">{post.title}</h5>
        <p className="card-text">{post.text}</p>
        {categories.map((category) => {
          if (category.id === post.categoryId) {
            <span className={setCategoryClassName(category)} key={category.id}>
              {category.name}
            </span>;
          }
        })}

        <button onClick={handleEdit} className="btn btn-success m-2">
          Редактировать пост
        </button>

        <button
          type="button"
          onClick={handleClick}
          className="btn btn-danger m-2"
        >
          Удалить
        </button>
      </div>
    </div>
  ) : (
    "loading"
  );
};

export default PostCard;
