import React, { useEffect } from "react";
import TextField from "../fields/textField";
import api from "../../api/index";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { setAuthToken } from "../setAuthToken";
import inMemoryJWT from "../../services/inMemoryJWT";

const LoginForm = () => {
  const navigate = useNavigate();
  const [isLogged, setIsLogged] = useState();
  const [users, setUsers] = useState();
  const [data, setData] = useState({
    login: "",
    password: "",
  });

  useEffect(() => {
    let isMounted = true;
    api.data.getIsLogged().then((data) => setIsLogged(data));
    return () => {
      isMounted = false;
    };
  }, []);

  //   const handleLogin = (id) => {
  //     api.data.setIsLogged(id).then((data) => console.log(data));
  //   };
  //   console.log(isLogged);

  const handleChange = (target) => {
    setData((prevState) => ({
      ...prevState,
      [target.name]: target.value,
    }));
    console.log(data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let id = null;

    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/v1/token/",
        {
          username: data.login,
          password: data.password,
        },
        { credentials: "include" }
      );

      localStorage.setItem("refresh", response.data.refresh);

      const user = await axios.get(
        "http://127.0.0.1:8000/api/v1/User/profile/"
      );

      id = user.data;
      inMemoryJWT.setToken(response.data.access);
    } catch (error) {
      console.log(error);
    }

    if (id) {
      localStorage.setItem("id", id);
      navigate(`/profile/${id}`, { replace: true });
      //   localStorage.setItem("id", id);
      document.location.reload();
    } else {
      console.log("user not found");
    }
  };

  return (
    !isLogged && (
      <div className="m-4 p-4">
        <section className="shadow m-4 p-4">
          <form className="g-3" onSubmit={handleSubmit}>
            <h1>Login Form</h1>
            <TextField
              type="text"
              label="Логин"
              name="login"
              onChange={handleChange}
              value={data.login}
            />

            <TextField
              type="password"
              label="Пароль"
              name="password"
              onChange={handleChange}
              value={data.password}
            />

            <div className="col-12">
              <button className="btn m-2 btn-sm btn-outline-dark">
                Sign in
              </button>
            </div>
          </form>
        </section>
      </div>
    )
  );
};

export default LoginForm;
