import axios from "axios";

const inMemoryJWTService = () => {
  let inMemoryJWT = null;
  let refreshTimeoutId = null;

  const refreshToken = () => {
    const timeoutTrigger = 4 * 60 * 1000;

    refreshTimeoutId = setInterval(() => {
      axios
        .post(
          "http://127.0.0.1:8000/api/v1/refresh/",
          localStorage.getItem("refresh")
        )
        .then((response) => setToken(response.data.access));
    }, timeoutTrigger);
  };

  const abortRefeshToken = () => {
    if (refreshTimeoutId) clearInterval(refreshTimeoutId);
  };

  const getToken = () => inMemoryJWT;

  const setToken = (token) => {
    inMemoryJWT = token;
    setAuthToken();
    refreshToken();
  };

  const deleteToken = () => {
    inMemoryJWT = null;
    abortRefeshToken();
  };

  const setAuthToken = () => {
    if (inMemoryJWT) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${inMemoryJWT}`;
    } else delete axios.defaults.headers.common["Authorization"];
  };

  return getToken, setToken, deleteToken;
};

export default inMemoryJWTService();
