import React, { useContext } from "react";
import api from "../api";
import PostCard from "./postCard";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";
import { v4 as uuidv4 } from "uuid";
import CommentForm from "./commentForm";
import backgroundImage from "../images/hero-banner.png";

const PostsPage = () => {
  const [isLogged, setIsLogged] = useState();
  const [users, setUsers] = useState([]);
  const [posts, setPosts] = useState([]);

  const [articles, setArticles] = useState({});
  useEffect(() => {
    let isMounted = true;

    // axios
    //   .get("http://127.0.0.1:8000/api/v1/Article/1/")
    //   .then((data) => setArticles(data.data));

    api.data.fetchAllPosts().then((data) => {
      if (isMounted) setPosts(data);
    });
    return () => {
      isMounted = false;
    };
  }, []);

  useEffect(() => {
    let isMounted = true;
    api.data.fetchAllUsers().then((data) => {
      if (isMounted) setUsers(data);
    });
    return () => {
      isMounted = false;
    };
  }, []);

  useEffect(() => {
    let isMounted = true;
    api.data.getIsLogged().then((data) => setIsLogged(data));
    return () => {
      isMounted = false;
    };
  }, []);

  const getComments = (comment) => {
    const user = users.find((user) => user.id === comment.userId);
    return (
      <div key={comment.commentId} className="card-footer m-2">
        <p>{user.name + " " + user.surname}</p>
        <p>{comment.commentText}</p>
      </div>
    );
  };

  console.log(articles);
  return users && posts ? (
    <>
      <section class="mb-30px">
        <img src={backgroundImage} class="img-fluid center-block" />
        <div class="container">
          <div class="hero-banner">
            <div class="hero-banner__content">
              <h3>Meet The Beauty</h3>
              <h1>Beyond Ruins</h1>
            </div>
          </div>
        </div>
      </section>
      <div>
        <h1 className="mb-10">Posts</h1>
        {users.map((user) => {
          return (
            posts.find((post) => post.userId === user.id) &&
            user.id !== isLogged && (
              <div key={user.id} className="card m-4 p-2">
                <h3>{user.name + " " + user.surname}</h3>
                {posts.map((post) => {
                  if (post.userId === user.id) {
                    return (
                      post && (
                        <div key={post.id}>
                          <PostCard postId={post.id} />
                          {isLogged && (
                            <CommentForm
                              commentId={uuidv4()}
                              postId={post.id}
                              userId={isLogged}
                            />
                          )}
                        </div>
                      )
                    );
                  }
                })}
              </div>
            )
          );
        })}
      </div>
    </>
  ) : (
    "Loading"
  );
};

export default PostsPage;
