import React, { useContext } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api/fake.api/users.api";
import { useState } from "react";
import { useEffect } from "react";
import PostCard from "./postCard";
import axios from "axios";
import inMemoryJWT from "../services/inMemoryJWT";

const UserPage = ({ userId }) => {
  const [user, setUser] = useState();
  const [posts, setPosts] = useState();
  const [isLogged, setIsLogged] = useState();

  useEffect(() => {
    try {
      axios
        .get(`http://127.0.0.1:8000/api/v1/User/${userId}/`)
        .then((data) => setUser(data.data));
    } catch (error) {
      console.log(error);
    }
  }, []);

  useEffect(() => {
    let isMounted = true;
    axios
      .get(`http://127.0.0.1:8000/api/v1/User/${userId}/post`)
      .then((data) => setPosts(data.data || null));
    return () => {
      isMounted = false;
    };
  }, []);

  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/home/${user.id}/addPost`, { replace: true });
  };

  const handleComeBack = () => {
    navigate(`/home/${user.id}/posts`, { replace: true });
  };

  return posts && user ? (
    <div className="m-4 p-4">
      <div className="shadow m-4 p-4">
        <h1 className="title">My posts</h1>
        <h5>{user.login}</h5>

        {posts &&
          posts.map((post) => (
            <div key={post.id} className="card">
              <PostCard postId={post.id} />
            </div>
          ))}

        <button
          onClick={handleClick}
          className="btn btn-sm btn-outline-dark m-2"
        >
          Создать пост
        </button>

        <button
          onClick={handleComeBack}
          className="btn btn-sm btn-outline-dark m-2"
        >
          Ко всем постам
        </button>
      </div>
    </div>
  ) : (
    "loading..."
  );
};

export default UserPage;
