import axios from "axios";

const inMemoryJWTService = () => {
  let inMemoryJWT = null;
  let refreshTimeoutId = null;
  let rfrToken = null;

  const refreshToken = (rfrToken) => {
    const timeoutTrigger = 5 * 60 * 1000 - 10000;

    refreshTimeoutId = setInterval((rfrToken) => {
      axios
        .post("http://127.0.0.1:8000/api/v1/refresh/", rfrToken)
        .then((response) => setToken(response.data.access));
    }, timeoutTrigger);
  };

  const abortRefeshToken = () => {
    if (refreshTimeoutId) clearInterval(refreshTimeoutId);
  };

  const getToken = () => inMemoryJWT;

  const getRefreshToken = () => rfrToken;

  const setRefreshToken = (token) => {
    rfrToken = token;
  };

  const setToken = (token) => {
    inMemoryJWT = token;
    refreshToken();
  };

  const deleteToken = () => {
    inMemoryJWT = null;
    abortRefeshToken();
  };

  return getToken, setToken, deleteToken, setRefreshToken, getRefreshToken;
};

export default inMemoryJWTService();
